package Array;

public class foreachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int [] a= {10,20,30};
		
		for(int temp:a)//foreach we can temp we can write anything and we declare array variable a
			//temp will  will retrive or access 3 value of a 
		{
			System.out.println(temp);//10,20,30
		}
		
		int []b= {20,30};
		
		for(int temp1:b)
		{
			System.out.println(temp1);
		}

	}

}
