package Basicloops;

public class Switchtatment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int day=8;
		
		switch(day)
		{
		case 1:
			System.out.println("sunday");
			break;
		case 2:
			System.out.println("monday");
			break;
		default:
		System.out.println("no day");
		
		}
	}

}
