package String;

public class concat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//concat -we can concate string literal text to end of another string literal text 
		String a="Shekhar Nanaware";
		String b=" ";
		String c="Akshay Bhumkar";
		
		System.out.println(a.concat(b).concat(c));
		
		
		//other method using +
		System.out.println(a+b+c);
	}

}
