package String;

public class isempty {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//iseMPTY:The purpose is to check whether the given string literal text is empty or not
		
		String a="Shekhar Nanaware";
		String b="";
		
		System.out.println(a.isEmpty());//false
		System.out.println(b.isEmpty());//true
		//its is empty that reason output is true
	}

}
