package String;

public class length {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// lenghth-it will return the size or length of string
		String a="Shekhar";
		
		System.out.println(a.length());//7
		
		String b="Nanaware shekhar";
		System.out.println(b.length());// 16 midddle space also counted
		
		String c=" Nanaware shekhar ";
		System.out.println(c.length());//before and after space is counted
		
	}

}
