package String;

public class startwith {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//startwith():to check whether the given string literal is starting with given text
		
		String a="Shekhar nanaware";
		
		System.out.println(a.startsWith("She"));
		System.out.println(a.startsWith("nana", 8));
		
	}

}
