package String;

public class substring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String a="Shekhar Nanaware";
		
		System.out.println(a.substring(8));//substring will used print the value wherever from you need
		System.out.println(a.substring(0, 7));// it will print first 7 words
		
	}

}
