package String;

public class tostring {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//tostring:to convert other literal to string
		//int literal,boolean literal,floating point literal,character literal
		
		Integer a=9;
		String s=a.toString();
		System.out.println(s);
		
		Short c=5;
		String b=c.toString();
		System.out.println(b);
		Float e=1.25F;
		String p=e.toString();
		System.out.println(p);
		Character g='s';
		String ps=g.toString();
		System.out.println(ps);
		Boolean h=true;
		String bn=h.toString();
		System.out.println(bn);
		
	}

}
