package String;

public class touppercaseandlowercase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//touppercase:to convert all the letter in the given string literal into uppercase letter
		//tolowercase:to convert all the letter in the given string literal into lowercase letter
		String a="Shekhar nanaware";
		
		System.out.println(a.toUpperCase());
		System.out.println(a.toLowerCase());
	}

}
